<?php #login.inc ?><div class="login"><div class="login-title">Login</div><form action="<?=site_url("user/login"); ?>" method="post"	name="login_form" id="login_form"><?if($errors):	if(is_array($errors)){		foreach($errors as $msg){			$output =  " -$msg<br/>\n";		}	}else{		$output =  "$errors";	}	?>	<div class="error-text"><? print $output; ?></div><?endif;?><div class='login-inputs'><p><label for="username">Username</label><br /><input type="text" name="username" id="username"	value="<? $username; ?>" class="login-text" /></p><p><label for="password">Password</label><br/><input type="password" name="password" id="password" class="login-text" /></p><p>First Time Loggin In?/Forgot Your Password? <a href="<?=site_url("user/start_reset");?>">Click Here</a></p><p><input type="submit" name="submit" class="button" value="Login" /></p></div></form></div>