<?php #verify.php ?><div class="login"><div class="login-title">Verify Account</div><form action="<?php echo site_url("user/start_verify"); ?>" method="post"	name="login_form" id="login_form"><?phpif($errors):	if(is_array($errors)){		foreach($errors as $msg){			$output =  " -$msg<br/>\n";		}	}else{		$output =  "$errors";	}	?> 	<div class="error-text"><?php print $output; ?></div><?phpendif;?><div class='login-inputs'><p><label for="user_info">Email Address or Username</label><br /><input type="text" name="user_info" id="user_info"	value="" class="login-text" /></p><p>	<label for="password">Choose a Password: </label><br/><input type="password" id="password" name="password" class="login-text" value=""/></p><p><label for="check_password">Re-enter Password: </label><br/><input type="password" id="check_password" name="check_password" class="login-text" value=""/></p><p><span id='password_note' class='notice' style="display:none"></span></p><p><input type="submit" name="submit" class="button" value="Verify" /></p></div></form></div>