<?php$levels = array("","Low","Medium","High");$level_options = "";foreach($levels as $level){	$selected = "";	if($level == $urgency){		$selected = " selected";	}	$level_options .= "<option value='$level' $selected>$level</option>";}?><div id="feedback-div"><form id="feedback-editor" action="<?=site_url("feedback/add");?>"	method="post"  name="feedback-editor">	<p>		<label for="subject">Subject:</label>		<input type="text" name="subject" id="subject" style="width: 95%" value="<?=$subject;?>"/>	</p>	<p>		<label for="rank">Urgency</label>		<select name="rank" id="rank" size="1">			<?=$level_options;?>		</select>	</p>	<p>		<textarea name="feedback" id="feedback" rows="13" style="width: 95%"><? print $feedback; ?></textarea>	</p>	<p>		<span class="button send_feedback">Submit</span>	</p></form></div>